import { makeExecutableSchema } from 'graphql-tools';

import { PubSub, withFilter } from 'graphql-subscriptions';

const pubsub = new PubSub();
const ADDED_NEW_BLOG_POST = 'new_blog_post';
const counter = 3;

const schemaString = `
schema {
  query: Query
  mutation: Mutation
  subscription: Subscription
}

type Query {
  blogPosts: BlogPosts
  blogPost(id: String): BlogPost
}

type Mutation {
  addBlogPost(title: String, content: String, author: String): BlogPost
}

type Subscription {
  newBlogPost: BlogPost
}

type BlogPost {
  _id: String
  title: String
  content: String
  author: String
}

type Aggregate {
  count: String
}

type BlogPosts {
  nodes: [BlogPost]
  aggregate: Aggregate
}
`;

const blogPosts = [
  {
    id: '1000',
    title: 'Blog Post 1',
    content : 'This is the first blog post',
  },
  {
    id: '1001',
    title: 'Blog Post 2',
    content : 'This is the second blog post',
  },
];

const blogPostData = {};
blogPosts.forEach((post) => {
  blogPostData[post.id] = post;
});

function getPost(id) {
  return blogPostData[id];
}

function getAllPosts() {
    return blogPostData;
}

const resolvers = {
  Query: {
    blogPosts: (_root, _args) => getAllPosts(),
    blogPost: (_root, { id }) => getPost(id),
  },
  Mutation: {
    addBlogPost: (_root, { title, content, author }) => {
      console.log(`Adding blog post: ${title}`);
      const id = counter;
      const post = { id: counter, title: title, content: content, author: author}
      blogPostData[id].push(post);
      pubsub.publish(ADDED_NEW_BLOG_POST, {addBlogPost: post});
      counter ++;
      return post;
    },
  },
  Subscription: {
    newBlogPost: {
        subscribe: withFilter(
            () => pubsub.asyncIterator(ADDED_NEW_BLOG_POST),
        ),
    },
  },
}

export const NewsAppSchema = makeExecutableSchema({
  typeDefs: [schemaString],
  resolvers
});

